mapper.select:
SELECT *
FROM Emp
WHERE 1=1

    AND name like CONCAT('%',#{name},'%')


    AND phone like CONCAT('%',#{phone},'%')


    AND gender = #{gender}


    AND isFavorite = #{isFavorite}

limit #{start},#{pageSize}

mapper.update:
UPDATE Emp
SET name = #{name},
    phone = #{phone},
    gender = #{gender},
    isFavorite = #{isFavorite}
WHERE id = #{id}
